import React, { useState, useEffect } from 'react';
import { getStorage } from '../utils/storage';
import api from '../services/api';

const handleLogin = async e => {
  e.preventDefault();
  try {
    const res = await api.post('/auth/login', {
      username,          // o nombre_usuario
      password,
    });
    localStorage.setItem('token', res.data.token);
    setUser(res.data.user);        // viene { id, username, role }
    setCurrentPage('invoices');    // o la vista que desees
  } catch (err) {
    setError('Usuario o contraseña incorrectos');
  }
};

const LoginScreen = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [users, setUsers] = useState([]); // Estado para almacenar los usuarios cargados
  const [loading, setLoading] = useState(true); // Estado para controlar la carga de usuarios

  useEffect(() => {
    const loadUsers = async () => {
      try {
        const loadedUsers = await getStorage('users');
        setUsers(loadedUsers || []);
      } catch (err) {
        console.error("Error loading users for login:", err);
        setError("Error al cargar usuarios. Intenta de nuevo.");
      } finally {
        setLoading(false);
      }
    };
    loadUsers();
  }, []); // Se ejecuta solo una vez al montar el componente

  const handleLogin = () => {
    if (loading) {
      setError('Cargando usuarios, por favor espera...');
      return;
    }

    const user = users.find(
      (u) => u.username === username && u.password === password
    );

    if (user) {
      setError('');
      onLogin(user);
    } else {
      setError('Usuario o contraseña incorrectos.');
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-sm text-center">
        <img src="https://4tsix0yujj.ufs.sh/f/2vMRHqOYUHc0uSCql6YvblOpQY7AJg0nTmsBhyINFZ6E3XCU" alt="Porkiapp Logo" className="mx-auto mb-4 w-24 h-24 object-contain" />
        <h1 className="text-3xl font-bold text-gray-800 mb-6">Porkiapp</h1>

        <div className="mb-4">
          <label htmlFor="username" className="sr-only">Nombre de Usuario</label>
          <input
            type="text"
            id="username"
            placeholder="Nombre de Usuario"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-black transition"
          />
        </div>
        <div className="mb-6">
          <label htmlFor="password" className="sr-only">Contraseña</label>
          <input
            type="password"
            id="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-black transition"
          />
        </div>

        {error && <p className="text-red-600 text-sm mb-4">{error}</p>}
        {loading && <p className="text-gray-500 text-sm mb-4">Cargando...</p>}

        <button
          onClick={handleLogin}
          className="w-full px-4 py-2 bg-black text-white rounded-lg hover:bg-gray-800 transition-colors"
          disabled={loading} // Deshabilitar botón mientras carga
        >
          Iniciar Sesión
        </button>
      </div>
    </div>
  );
};

export default LoginScreen;
