import React, { useState, useEffect } from 'react';
import LayoutHeader from './components/LayoutHeader';
import InvoiceList from './components/InvoiceList';
import DeboningForm from './components/DeboningForm';
import InventoryList from './components/InventoryList';
import OrderForm from './components/OrderForm';
import ManagementModule from './components/ManagementModule';
import LoginScreen from './components/LoginScreen';
import { initializeDb } from './utils/storage'; // Importar la función de inicialización



const App = () => {
  const [currentPage, setCurrentPage] = useState('invoices');
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // Nuevo estado para controlar la carga inicial

  useEffect(() => {
    const initApp = async () => {
      await initializeDb(); // Inicializar la base de datos simulada
      setLoading(false); // Marcar como cargado
    };
    initApp();
  }, []);

  const handleLogin = (authenticatedUser) => {
    setUser(authenticatedUser);
    setCurrentPage('invoices');
  };

  const handleLogout = () => {
    setUser(null);
    setCurrentPage('invoices');
  };

  const renderPage = () => {
    if (loading) {
      return (
        <div className="flex items-center justify-center min-h-screen">
          <p className="text-xl font-semibold text-gray-700">Cargando aplicación...</p>
        </div>
      );
    }

    if (!user) {
      return <LoginScreen onLogin={handleLogin} />;
    }

    if (user.role === 'Operario' && currentPage === 'management') {
       setCurrentPage('invoices');
       return <InvoiceList />;
    }

    switch (currentPage) {
      case 'invoices':
        return <InvoiceList />;
      case 'deboning':
        return <DeboningForm />;
      case 'inventory':
        return <InventoryList />;
      case 'orders':
        return <OrderForm />;
      case 'management':
        return <ManagementModule />;
      default:
        return <InvoiceList />;
    }
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      {user && <LayoutHeader currentPage={currentPage} setCurrentPage={setCurrentPage} userRole={user.role} onLogout={handleLogout} />}
      <main className="flex-grow container mx-auto py-6">
        {renderPage()}
      </main>
    </div>
  );
};

export default App;
