// src/components/LoginScreen.js
import React, { useState } from 'react';
import api from '../services/api';

const LoginScreen = ({ setUser, setCurrentPage }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error,    setError]    = useState('');

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const { data } = await api.post('/auth/login', {
        username,          // el back lo reconoce
        password
      });
      localStorage.setItem('token', data.token);
      setUser(data.user);                 // { id, username, role }
      api.defaults.headers.common.Authorization = `Bearer ${data.token}`;
      setCurrentPage('invoices');         // o la vista que quieras abrir
    } catch (err) {
      console.error(err);
      setError('Usuario o contrase침a incorrectos');
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <form
        onSubmit={handleSubmit}
        className="bg-white p-8 rounded shadow-md w-80"
      >
        <h1 className="text-2xl font-bold mb-6 text-center">Iniciar sesi칩n</h1>

        {error && (
          <p className="mb-4 text-red-600 text-center">{error}</p>
        )}

        <label className="block mb-2 text-sm font-medium text-gray-700">
          Usuario
        </label>
        <input
          type="text"
          value={username}
          onChange={e => setUsername(e.target.value)}
          className="w-full mb-4 px-3 py-2 border rounded"
        />

        <label className="block mb-2 text-sm font-medium text-gray-700">
          Contrase침a
        </label>
        <input
          type="password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          className="w-full mb-6 px-3 py-2 border rounded"
        />

        <button
          type="submit"
          className="w-full bg-black text-white py-2 rounded hover:bg-gray-800 transition-colors"
        >
          Entrar
        </button>
      </form>
    </div>
  );
};

export default LoginScreen;
